object HasHackerRank {
  def main(args: Array[String]) {
    println(hackerrankInString("hereiamstackerrank"))
    println(hackerrankInString("" ))
  }
  def hackerrankInString(s:String):String ={
    var x="hackerrank"
    var j =0
    for (i<-  s){
      if(i == x(j))
        j +=1
      if(j>=10){
        if(j==10)
          return "YES"
        else
          return "NO"
      }
    }
    "NO"
  }
}
